import React from 'react';
import CharacterItem from './CharacterItem';

const CharacterGrid = (props) => {
  const { isLoading, items, flagItems } = props;
  return isLoading ? (
    <h1>Loading....</h1>
  ) : (
    <section className='cards'>
      {items.map(
        (item) => {
          return <CharacterItem key={item.CountryCode} item={item} />;
        },
        flagItems.map((flagItem) => {
          return <CharacterItem flagItem={flagItem} />;
        })
      )}
    </section>
  );
};

export default CharacterGrid;
